import "./Footer.css";

type FooterPageProps = {
  id?: string;

  // top bar links
  privacyHref?: string;
  termsHref?: string;

  // nav links
  nav?: { label: string; href: string }[];

  // social links
  socials?: { label: string; href: string }[];

  // bottom left addresses
  addresses?: string[];

  // bottom right copyright
  copyrightTop?: string;
  copyrightBottom?: string;
};

import React from "react";

type Props = {
  href: string;
  children: React.ReactNode;
  label: string;
};

export function SocialLink({ href, children, label }: Props) {
  return (
    <a
      href={href}
      target="_blank"
      rel="noopener noreferrer"
      aria-label={label}
      className="wfSocial"
    >
      {children}
    </a>
  );
}

